sysauto_script.condition:
//Create the answer varaible
var answer = true;

//Get today's date and determine which day of the week it is
var rightNow = new GlideDateTIme();
var dayOfWeek = rightnow.getDayofWeekLocalTIme();

//If today is Saturday (6) or Sunday (7), set the answer variable to false
//so the Run this script field's script doesn't execute.
//Monday = 1, Tuesday = 2 etc.
if (dayOfWeek == 6 || dayofWeek == 7){
	answer = false;
}
//End script with something that resolves to true or false. Ignore the warning. 
answer;

sysauto_script.script:
//Query the database for NeedIt Task records with Due date field values older
//then the current time. Only return NeedIT Task records that do not have
//a State field value of Closed Complete.
var ovberdueNITasks = new GlideRecord('x_58872_needit_needit_task');
overdueNITasks.addQuery('due_date','<=',rightNow);
overdueNITasks.addQuery('state','!=', 3);

overdueNITasks.query();
//Write a log message for each overdue NeedIT Task Record
while(overdueNITasks.next()){
	gs.info("Overdue NeedIt Task record = " + overdueNITasks.number);
}
